function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
	//var gd = new GlideDate();
   var whatneeded = g_form.getValue('u_what_needed');
   g_form.clearOptions('u_what_needed');
   if(newValue=='hr'){
	g_form.addOption('u_what_needed','hr1','Human Resource 1');
	g_form.addOption('u_what_needed','hr2','Human Resource 2');
	g_form.addOption('u_what_needed','other','Other');
   }

   if(newValue=='facilities'){
	g_form.addOption('u_what_needed','facilities1','Facilities 1');
	g_form.addOption('u_what_needed','facilities2','Facilities 2');
	g_form.addOption('u_what_needed','other','Other');
   }
    if(newValue=='legal'){
	g_form.addOption('u_what_needed','legal1','legal 1');
	g_form.addOption('u_what_needed','legal2','legal 2');
	g_form.addOption('u_what_needed','other','Other');
   }

   if(isLoading && !g_form.isNewRecord){
	alert("What needed is "+ whatneeded);
	g_form.setValue('u_what_needed', whatneeded);
   }

   var user_firstname = g_user.firstName;
   var user_lastName= g_user.lastName;
   var user_Fullname = g_user.getFullName();
   var user_Id= g_user.userID;
   var user_username= g_user.userName;
   var user_hasAnyRole=g_user.hasRoles();
   g_form.clearOptions('description');
   g_form.setValue('description',user_hasAnyRole);


	g_form.addErrorMessage("Hi This is Error Msg");
	g_form.addInfoMessage("Hi This is Information");
	g_form.clearMessages();
	//alert("get Action "+g_form.getActionName());
	//alert( " get control: -> " + g_form.getControl('u_what_needed'));
   //Type appropriate comment here, and begin script below
   if(g_form.isNewRecord){

	g_form.setValue('u_requested_for',g_user.getFullName(),g_user.getFullName());
	//g_form.setValue('u_when_needed',gd.getValue(),gd.getValue());
   }

}
